-- Create twStocks table
CREATE TABLE IF NOT EXISTS `twStocks` (
  `id` int AUTO_INCREMENT NOT NULL,
  `symbol` varchar(10) NOT NULL,
  `name` varchar(100) NOT NULL,
  `shortName` varchar(50),
  `market` enum('TWSE','TPEx') NOT NULL,
  `industry` varchar(50),
  `isActive` boolean NOT NULL DEFAULT true,
  `listedDate` date,
  `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updatedAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  CONSTRAINT `twStocks_id` PRIMARY KEY(`id`),
  CONSTRAINT `twStocks_symbol_unique` UNIQUE(`symbol`)
);

CREATE INDEX IF NOT EXISTS `symbol_idx` ON `twStocks` (`symbol`);
CREATE INDEX IF NOT EXISTS `market_idx` ON `twStocks` (`market`);
CREATE INDEX IF NOT EXISTS `industry_idx` ON `twStocks` (`industry`);
CREATE INDEX IF NOT EXISTS `isActive_idx` ON `twStocks` (`isActive`);

